mapper.select:
select j.*, g.user_id as sg_user_id, g.con_nm, g.sg_name, ifnull(c.sg_aplynum, 0) as sg_aplynum, g.sg_allnum, g.sg_start_date, g.sg_end_date, g.sg_level, g.sg_content, g.sg_status, u.user_nm from join_group j left join study_group g on j.sg_no = g.sg_no left join user u on j.user_id = u.user_id left join (select count(*) as sg_aplynum, sg_no from join_group where jg_confirm = '승인' group by sg_no) c on j.sg_no = c.sg_no where g.user_id = #{userId} and j.jg_confirm = '처리중'
and u.user_nm LIKE concat('%',#{search.user_nm},'%')
order by u.user_nm limit #{cri.startPage}, #{cri.showPage}

mapper.update:
update study_group set sg_name = #{sg_name}, sg_allnum = #{sg_allnum}, sg_level = #{sg_level},
                       sg_content = #{sg_content}, con_nm= #{con_nm}, sg_start_date = #{sg_start_date},
                       sg_end_date = #{sg_end_date}, sg_status = #{sg_status}
where sg_no = #{sg_no}

mapper.select:
        select c.*, cc.cate_nm from cont_buy cb
        left join content c on c.con_nm=cb.con_nm
        left join cont_cate cc on c.cate_no = cc.cate_no
        where user_id = #{userId}
        
            and c.con_nm LIKE concat('%',#{search.con_nm},'%')
            limit #{cri.startPage}, #{cri.showPage}
