mapper.select:
SELECT *
FROM hw_distribution
WHERE h_no IN
      (SELECT h_no FROM hw WHERE sg_no IN
                                 (SELECT sg_no FROM join_group WHERE user_id = #{user_id})
      )LIMIT #{cri.page}, #{cri.amount};

SELECT count(*)
FROM hw_distribution
WHERE h_no IN
      (SELECT h_no FROM hw WHERE sg_no IN
                                 (SELECT sg_no FROM join_group WHERE user_id = #{user_id})
      );
