mapper.select:
select * from question qq left join ( select count(*) as a_count, q.q_no from answer a left join question q on a.q_no = q.q_no group by q.q_no ) aa on qq.q_no = aa.q_no where 1=1
and q_context LIKE CONCAT('%', #{questionSearch}, '%') or q_title LIKE CONCAT('%', #{questionSearch}, '%')
and status = #{status}
order by qq.q_no desc Limit #{cri.startPage},#{cri.writing}

select count(*) from question where 1=1
and q_context LIKE CONCAT('%', #{questionSearch}, '%') or q_title LIKE CONCAT('%', #{questionSearch}, '%')
and status = #{status}

select * from answer
where q_no = #{q_no}
order by a_no desc;
